async function login(e){const n=document.querySelector(".error-display");xhr=new XMLHttpRequest,event.preventDefault();var o={username:e.elements.username.value,password:e.elements.password.value};let t=[];for(property in o){var r=encodeURIComponent(property),s=encodeURIComponent(o[property]);t.push(r+"="+s)}t=t.join("&");try{xhr.open("POST","/login"),xhr.setRequestHeader("Content-Type","application/x-www-form-urlencoded"),xhr.onload=function(){200==this.status&&"logged in"===this.responseText?window.location.replace("/"):n.innerHTML=this.responseText},xhr.send(t)}catch(e){console.log(e)}}