function login(e){xhr=new XMLHttpRequest;sendData("/login",{username:e.elements.username.value,password:e.elements.password.value})}async function sendData(e,n){xhr=new XMLHttpRequest;const o=document.querySelector(".error-display");let t=[];for(property in n){var r=encodeURIComponent(property),s=encodeURIComponent(n[property]);t.push(r+"="+s)}t=t.join("&");try{xhr.open("POST",e),xhr.setRequestHeader("Content-Type","application/x-www-form-urlencoded"),xhr.onload=function(){200==this.status&&"logged in"===this.responseText?window.location.replace("/"):o.innerHTML=this.responseText},xhr.send(t)}catch(e){console.log(e)}}